import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet, ActivityIndicator, Switch } from 'react-native';
import { useAuth } from './AuthContext';
import { supabase } from './supabase';
import { useTheme } from './ThemeContext';

export default function RegisterScreen({ navigation }) {
  const { signUp } = useAuth();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [category, setCategory] = useState('');
  const [isVendor, setIsVendor] = useState(false);
  const [loading, setLoading] = useState(false);
  const { theme } = useTheme();
  const styles = getStyles(theme);

const handleRegister = async () => {
  setLoading(true);

  // Step 1: Sign up the user
  const { data: signup, error: signupError } = await supabase.auth.signUp({
    email: email.trim(),
    password: password.trim(),
  });

  if (signupError) {
    alert(signupError.message);
    setLoading(false);
    return;
  }

  const userId = signup?.user?.id;

  // Optional wait to ensure user is synced before inserting vendor
  await new Promise((res) => setTimeout(res, 300));

  // Step 2: If vendor, insert into vendors table
  if (isVendor && userId) {
    const { error: vendorError } = await supabase.from('vendors').insert([
      {
        id: userId,
        email: email.trim(),
        name: name.trim(),
        category: category.trim(),
      },
    ]);

    if (vendorError) {
      alert(`Vendor creation failed: ${vendorError.message}`);
      setLoading(false);
      return;
    }
  }

  setLoading(false);
  navigation.navigate('Login'); // or 'Home', depending on your flow
};


  return (
    <View style={styles.container}>
      <Text style={styles.header}>Register</Text>
      <TextInput
        style={styles.input}
        placeholder="Email"
        autoCapitalize="none"
        keyboardType="email-address"
        value={email}
        onChangeText={setEmail}
      />
      <TextInput
        style={styles.input}
        placeholder="Password"
        secureTextEntry
        value={password}
        onChangeText={setPassword}
      />
      <View style={styles.switchRow}>
        <Text style={{ color: theme.text }}>Register as Vendor</Text>
        <Switch value={isVendor} onValueChange={setIsVendor} />
      </View>
      {isVendor && (
        <>
          <TextInput
            style={styles.input}
            placeholder="Name"
            value={name}
            onChangeText={setName}
          />
          <TextInput
            style={styles.input}
            placeholder="Category"
            value={category}
            onChangeText={setCategory}
          />
        </>
      )}
      {loading && <ActivityIndicator style={{ marginVertical: 10 }} />}
      <Button title="Register" onPress={handleRegister} />
    </View>
  );
}

const getStyles = theme =>
  StyleSheet.create({
    container: { flex: 1, padding: 20, backgroundColor: theme.background },
    header: { fontSize: 24, fontWeight: 'bold', marginBottom: 10, color: theme.text },
    input: { borderWidth: 1, borderColor: theme.border, padding: 10, borderRadius: 6, marginBottom: 10, color: theme.text },
    switchRow: { flexDirection: 'row', alignItems: 'center', marginBottom: 10, justifyContent: 'space-between' }
  });
